import React from 'react';
import './nav.css';
import { useState, useEffect} from 'react';

function Nav() {

    const text = [
        ' a videographer.',
        ' a developer.',
        ' a son.',
        ' part of a community.',
    ]

    const Typewriter = ({ text, delay, infinite }) => {
        const [currentText, setCurrentText] = useState('Justin');
        const [currentIndex, setCurrentIndex] = useState(0);

        useEffect(() => {
            let timeout;

            timeout = setTimeout(() => {
                setCurrentText(prevText => ' Justin.');
                setCurrentIndex(prevIndex => prevIndex);
              }, delay / 2);

            if (currentIndex < text.length) {
              timeout = setTimeout(() => {
                setCurrentText(text[currentIndex]);
                setCurrentIndex(prevIndex => prevIndex + 1);
              }, delay);

            } else if (infinite) { 
              setCurrentIndex(0);
              setCurrentText('');
            }

            return () => clearTimeout(timeout);
          }, [currentIndex, delay, infinite, text]);

          return <span>{currentText}</span>;
        };

    return (
    <div className="coverContainer">
        <div className="navbar">
            <ul className='navContainer'>
                <li>JUSTIN</li>
                <li>Home</li>
                <li>About Me</li>
                <li>Portfolio</li>
                <li>Resume</li>
                <button><li>CONTACT ME</li></button>
            </ul>
        </div>
        <div className="cover">
            <div className='nameContainer'>
                <h1 className='name'>Hello. I'm
                <Typewriter text={text} delay={1000} infinite={1}/></h1>
            </div>
        </div>
    </div>

  );
};

export default Nav;
